import React from 'react';
import { Typography, Grid } from '@material-ui/core';
import CardComponent from './Card/Card';
import styles from './Cards.module.css';

const Info = ({ data: { onTime, delayed, total, lastUpdate } }) => {
  if (!onTime && onTime !== 0) {
    return 'Loading...';
  }

  return (
    <div className={styles.container}>
      <Typography gutterBottom variant="h4" component="h2">Анализ пунктуальности</Typography>
      <Grid container spacing={3} justify="center">
        <CardComponent
          className={styles.infected}
          cardTitle="Вовремя"
          value={onTime}
          lastUpdate={lastUpdate}
          cardSubtitle="Количество рейсов, прилетевших/вылетевших вовремя (до 15 минут задержки)."
        />
        <CardComponent
          className={styles.recovered}
          cardTitle="С задержкой"
          value={delayed}
          lastUpdate={lastUpdate}
          cardSubtitle="Количество рейсов с задержкой более 15 минут."
        />
        <CardComponent
          className={styles.deaths}
          cardTitle="Всего рейсов"
          value={total}
          lastUpdate={lastUpdate}
          cardSubtitle="Общее количество рейсов в системе."
        />
      </Grid>
    </div>
  );
};

export default Info;
