import React from 'react';
import { Typography, Grid } from '@material-ui/core';
import CardComponent from './Card/Card';
import styles from './Cards.module.css';

const Info = ({ data }) => {
  if (!data || !data.length) {
    return 'Loading...';
  }

  // Агрегируем
  const total = data.reduce((sum, a) => sum + a.totalFlights, 0);
  const onTime = data.reduce((sum, a) => sum + a.onTime, 0);
  const delayed = total - onTime;

  return (
    <div className={styles.container}>
<Typography
  gutterBottom
  variant="h4"
  component="h2"
  style={{ padding: '10px' }}
>
  Flight Stats
</Typography>
    <Grid container spacing={3} justify="center">
      <CardComponent
        className={styles.infected}
        cardTitle="Total Flights"
        value={total}
        cardSubtitle="All recorded flights from selected airports"
      />
      <CardComponent
        className={styles.recovered}
        cardTitle="On Time"
        value={onTime}
        cardSubtitle="Number of on-time departures"
      />
      <CardComponent
        className={styles.deaths}
        cardTitle="Delayed"
        value={delayed}
        cardSubtitle="Flights delayed over 15 minutes"
      />
    </Grid>
  </div>
  );
};

export default Info;
