import React, { useState, useEffect } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
  CartesianGrid,
} from 'recharts';
import useFlightStats from '../../shared/useFlightStats';

const delayStats = [
  { time: '08:00', onTime: 45, delayed: 10, canceled: 2 },
  { time: '09:00', onTime: 50, delayed: 8, canceled: 1 },
  { time: '10:00', onTime: 48, delayed: 12, canceled: 3 },
  { time: '11:00', onTime: 52, delayed: 6, canceled: 1 },
  { time: '12:00', onTime: 47, delayed: 15, canceled: 4 },
  { time: '13:00', onTime: 51, delayed: 9, canceled: 0 },
];

const getColor = (key) => {
  switch (key) {
    case 'green':
      return '#34c38f';
    case 'yellow':
      return '#f1b44c';
    case 'red':
      return '#f46a6a';
    case 'blue':
      return '#556ee6';
    default:
      return '#ccc';
  }
};

const th = {
  padding: '12px',
  borderBottom: '1px solid #ddd',
  textAlign: 'left',
  fontWeight: 600,
};

const td = {
  padding: '10px 12px',
  borderBottom: '1px solid #eee',
};

const summaryData = [
  { label: '–í—ã–ª–µ—Ç–µ–ª–∏ –≤–æ–≤—Ä–µ–º—è', value: 130, color: 'green' },
  { label: '–° –∑–∞–¥–µ—Ä–∂–∫–æ–π > 15 –º', value: 28, color: 'yellow' },
  { label: '–û—Ç–º–µ–Ω–µ–Ω—ã', value: 3, color: 'red' },
  { label: '–†–µ–π—Å–æ–≤ –≤ –≤–æ–∑–¥—É—Ö–µ —Å–µ–π—á–∞—Å', value: 57, color: 'blue'},
];

const airportsData = [
  { name: '–®–µ—Ä–µ–º–µ—Ç—å–µ–≤–æ', onTime: 92, avgDelay: 5, canceled: 5 },
  { name: '–î–æ–º–æ–¥–µ–¥–æ–≤–æ', onTime: 87, avgDelay: 8, canceled: 9 },
  { name: '–í–ª–∞–¥–∏–≤–æ—Å—Ç–æ–∫', onTime: 83, avgDelay: 9, canceled: 4 },
  { name: '–ö–∞–∑–∞–Ω—å', onTime: 81, avgDelay: 10, canceled: 7 },
  { name: '–ü—É–ª–∫–æ–≤–æ', onTime: 77, avgDelay: 12, canceled: 10 },
  { name: '–°–æ—á–∏', onTime: 75, avgDelay: 14, canceled: 8 },
];
const PunctualityDashboard = ({ handleCountryChange }) => {
  const [airportStats, setAirportStats] = useState(null);
  const [topAirlines, setTopAirlines] = useState([]);
  const [delayStats, setDelayStats] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/airports/SVO/stats')
      .then(res => res.json())
      .then(data => setAirportStats(data));

    fetch('http://localhost:8000/airlines/top')
      .then(res => res.json())
      .then(data => setTopAirlines(data));

    fetch('http://localhost:8000/airlines/AFL/delay-stats')
      .then(res => res.json())
      .then(data => setDelayStats(data));
  }, []);

  const delayPieData = delayStats?.category_counts
    ? Object.entries(delayStats.category_counts).map(([key, value]) => ({ name: key, value }))
    : [];

  return (
    <div className="dashboard-container">
      <h1>–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏</h1>

      <section className="dashboard-section">
        <h2>üìç –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø–æ –∞—ç—Ä–æ–ø–æ—Ä—Ç—É SVO</h2>
        {airportStats ? (
          <div className="airport-card">
            <p>–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏–π: {airportStats.departures}</p>
            <p>–ü—Ä–∏–±—ã—Ç–∏—è: {airportStats.arrivals}</p>
            <p>–ë–µ–∑ —Ñ–∞–∫—Ç–∞ –≤—ã–ª–µ—Ç–∞: {airportStats.missing_departure}</p>
            <p>–ë–µ–∑ —Ñ–∞–∫—Ç–∞ –ø—Ä–∏–ª–µ—Ç–∞: {airportStats.missing_arrival}</p>
          </div>
        ) : <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>}
      </section>

      <section className="dashboard-section">
        <h2>‚úàÔ∏è –¢–û–ü –∞–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏–π –ø–æ –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏</h2>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={topAirlines} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <XAxis dataKey="iata_code" />
            <YAxis domain={[0, 1]} />
            <Tooltip />
            <Legend />
            <Bar dataKey="rating_departure" fill="#00C49F" name="–í—ã–ª–µ—Ç" />
            <Bar dataKey="rating_arrival" fill="#FF8042" name="–ü—Ä–∏–ª–µ—Ç" />
          </BarChart>
        </ResponsiveContainer>
      </section>

      <section className="dashboard-section">
        <h2>‚è± –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∑–∞–¥–µ—Ä–∂–µ–∫ –¥–ª—è AFL</h2>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={delayPieData}
              cx="50%"
              cy="50%"
              labelLine={false}
              outerRadius={100}
              fill="#8884d8"
              dataKey="value"
              label={({ name }) => name}
            >
              {delayPieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
        <p>–°—Ä–µ–¥–Ω—è—è –∑–∞–¥–µ—Ä–∂–∫–∞: {delayStats?.average_delay_seconds} —Å–µ–∫</p>
      </section>
    </div>
  );
}

export default PunctualityDashboard;
