import React, { useState, useEffect } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
  CartesianGrid,
} from 'recharts';

const delayStats = [
  { time: '08:00', onTime: 45, delayed: 10, canceled: 2 },
  { time: '09:00', onTime: 50, delayed: 8, canceled: 1 },
  { time: '10:00', onTime: 48, delayed: 12, canceled: 3 },
  { time: '11:00', onTime: 52, delayed: 6, canceled: 1 },
  { time: '12:00', onTime: 47, delayed: 15, canceled: 4 },
  { time: '13:00', onTime: 51, delayed: 9, canceled: 0 },
];

const getColor = (key) => {
  switch (key) {
    case 'green':
      return '#34c38f';
    case 'yellow':
      return '#f1b44c';
    case 'red':
      return '#f46a6a';
    case 'blue':
      return '#556ee6';
    default:
      return '#ccc';
  }
};

const th = {
  padding: '12px',
  borderBottom: '1px solid #ddd',
  textAlign: 'left',
  fontWeight: 600,
};

const td = {
  padding: '10px 12px',
  borderBottom: '1px solid #eee',
};

const summaryData = [
  { label: '–í—ã–ª–µ—Ç–µ–ª–∏ –≤–æ–≤—Ä–µ–º—è', value: 130, color: 'green', icon: '‚úÖ' },
  { label: '–° –∑–∞–¥–µ—Ä–∂–∫–æ–π > 15 –º', value: 28, color: 'yellow', icon: '‚ö†Ô∏è' },
  { label: '–û—Ç–º–µ–Ω–µ–Ω—ã', value: 3, color: 'red', icon: '‚ùå' },
  { label: '–†–µ–π—Å–æ–≤ –≤ –≤–æ–∑–¥—É—Ö–µ —Å–µ–π—á–∞—Å', value: 57, color: 'blue', icon: 'üõ´' },
];

const airportsData = [
  { name: '–®–µ—Ä–µ–º–µ—Ç—å–µ–≤–æ', onTime: 92, avgDelay: 5, canceled: 5 },
  { name: '–î–æ–º–æ–¥–µ–¥–æ–≤–æ', onTime: 87, avgDelay: 8, canceled: 9 },
  { name: '–í–ª–∞–¥–∏–≤–æ—Å—Ç–æ–∫', onTime: 83, avgDelay: 9, canceled: 4 },
  { name: '–ö–∞–∑–∞–Ω—å', onTime: 81, avgDelay: 10, canceled: 7 },
  { name: '–ü—É–ª–∫–æ–≤–æ', onTime: 77, avgDelay: 12, canceled: 10 },
  { name: '–°–æ—á–∏', onTime: 75, avgDelay: 14, canceled: 8 },
];
const PunctualityDashboard = ({ handleCountryChange }) => {
  const [countries, setCountries] = useState([]);
  return (
    <div style={{ maxWidth: 1000, margin: '20px auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏</h2>

      <div style={{ display: 'flex', gap: 20, marginBottom: 30, flexWrap: 'wrap' }}>
        {summaryData.map(({ label, value, color, icon }) => (
          <div key={label} style={{
            flex: '1 1 200px',
            backgroundColor: getColor(color),
            color: 'white',
            padding: '15px 20px',
            borderRadius: 12,
            boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
          }}>
            <div style={{ fontSize: 18, marginBottom: 6 }}>{icon} {label}</div>
            <div style={{ fontSize: 24, fontWeight: 'bold' }}>{value} —Ä–µ–π—Å–æ–≤</div>
          </div>
        ))}
      </div>
      <h3 style={{ marginTop: 40 }}>–î–∏–Ω–∞–º–∏–∫–∞ –∑–∞–¥–µ—Ä–∂–µ–∫ –∑–∞ –¥–µ–Ω—å</h3>
<div style={{ width: '100%', height: 300 }}>
  <ResponsiveContainer>
    <LineChart data={delayStats}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="time" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey="onTime" stroke="#34c38f" name="–í–æ–≤—Ä–µ–º—è" strokeWidth={3} />
      <Line type="monotone" dataKey="delayed" stroke="#f1b44c" name="–° –∑–∞–¥–µ—Ä–∂–∫–æ–π" strokeWidth={3} />
      <Line type="monotone" dataKey="canceled" stroke="#f46a6a" name="–û—Ç–º–µ–Ω–µ–Ω—ã" strokeWidth={3} />
    </LineChart>
  </ResponsiveContainer>
</div>

      <h3>–°–≤–æ–¥–∫–∞ –ø–æ –∞—ç—Ä–æ–ø–æ—Ä—Ç–∞–º</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse', backgroundColor: '#fff' }}>
        <thead>
          <tr style={{ backgroundColor: '#f5f5f5' }}>
            <th style={th}>–ê—ç—Ä–æ–ø–æ—Ä—Ç</th>
            <th style={th}>% –≤–æ–≤—Ä–µ–º—è</th>
            <th style={th}>–°—Ä. –∑–∞–¥–µ—Ä–∂–∫–∞</th>
            <th style={th}>–û—Ç–º–µ–Ω–µ–Ω–æ</th>
          </tr>
        </thead>
        <tbody>
          {airportsData.map(({ name, onTime, avgDelay, canceled }) => (
            <tr key={name}>
              <td style={td}>{name}</td>
              <td style={td}>{onTime} %</td>
              <td style={td}>{avgDelay} –º–∏–Ω</td>
              <td style={td}>{canceled}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PunctualityDashboard;
