import React, { useState, useEffect } from 'react';
import { NativeSelect, FormControl } from '@material-ui/core';

import { fetchCountries } from '../../api';

import styles from './CountryPicker.module.css';


const getColor = (key) => {
  switch (key) {
    case 'green':
      return '#34c38f';
    case 'yellow':
      return '#f1b44c';
    case 'red':
      return '#f46a6a';
    case 'blue':
      return '#556ee6';
    default:
      return '#ccc';
  }
};


const summaryData = [
  { label: 'Вылетели вовремя', value: 130, color: 'green', icon: '✅' },
  { label: 'С задержкой > 15 м', value: 28, color: 'yellow', icon: '⚠️' },
  { label: 'Отменены', value: 3, color: 'red', icon: '❌' },
  { label: 'Рейсов в воздухе сейчас', value: 57, color: 'blue', icon: '🛫' },
];

const airportsData = [
  { name: 'Шереметьево', onTime: 92, avgDelay: 5, canceled: 5 },
  { name: 'Домодедово', onTime: 87, avgDelay: 8, canceled: 9 },
  { name: 'Владивосток', onTime: 83, avgDelay: 9, canceled: 4 },
  { name: 'Казань', onTime: 81, avgDelay: 10, canceled: 7 },
  { name: 'Пулково', onTime: 77, avgDelay: 12, canceled: 10 },
  { name: 'Сочи', onTime: 75, avgDelay: 14, canceled: 8 },
];
const PunctualityDashboard = ({ handleCountryChange }) => {
  const [countries, setCountries] = useState([]);
  return (
    <div style={{ maxWidth: 1000, margin: '20px auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>Ситуационный центр пунктуальности</h2>

      <div style={{ display: 'flex', gap: 20, marginBottom: 30, flexWrap: 'wrap' }}>
        {summaryData.map(({ label, value, color, icon }) => (
          <div key={label} style={{
            flex: '1 1 200px',
            backgroundColor: getColor(color),
            color: 'white',
            padding: '15px 20px',
            borderRadius: 12,
            boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
          }}>
            <div style={{ fontSize: 18, marginBottom: 6 }}>{icon} {label}</div>
            <div style={{ fontSize: 24, fontWeight: 'bold' }}>{value} рейсов</div>
          </div>
        ))}
      </div>

      <h3>Сводка по аэропортам</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse', backgroundColor: '#fff' }}>
        <thead>
          <tr style={{ backgroundColor: '#f5f5f5' }}>
            <th style={th}>Аэропорт</th>
            <th style={th}>% вовремя</th>
            <th style={th}>Ср. задержка</th>
            <th style={th}>Отменено</th>
          </tr>
        </thead>
        <tbody>
          {airportsData.map(({ name, onTime, avgDelay, canceled }) => (
            <tr key={name}>
              <td style={td}>{name}</td>
              <td style={td}>{onTime} %</td>
              <td style={td}>{avgDelay} мин</td>
              <td style={td}>{canceled}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PunctualityDashboard;
