import React from 'react';
import { Typography, Grid } from '@material-ui/core';
import CardComponent from './Card/Card';
import styles from './Cards.module.css';

const Info = ({ data }) => {
  if (!data || !data.length) {
    return 'Loading...';
  }

  // Агрегируем
  const total = data.reduce((sum, a) => sum + a.totalFlights, 0);
  const onTime = data.reduce((sum, a) => sum + a.onTime, 0);
  const delayed = total - onTime;

  return (
    <div className={styles.container}>
<Typography
  gutterBottom
  variant="h4"
  component="h2"
  style={{ padding: '20px', marginTop: '-100px' }}
>
  Flight Stats
</Typography>
<Grid container spacing={3} justify="center">
  <CardComponent
    className={styles.infected}
    cardTitle="Всего рейсов"
    value={total}
    cardSubtitle="Все зарегистрированные рейсы из выбранных аэропортов"
  />
  <CardComponent
    className={styles.recovered}
    cardTitle="Вовремя"
    value={onTime}
    cardSubtitle="Количество вылетов по расписанию"
  />
  <CardComponent
    className={styles.deaths}
    cardTitle="С задержкой"
    value={delayed}
    cardSubtitle="Рейсы с задержкой более 15 минут"
  />
</Grid>

  </div>
  );
};

export default Info;
