import React, { useState, useEffect } from 'react';
import { NativeSelect, FormControl } from '@material-ui/core';

import { fetchCountries } from '../../api';

import styles from './CountryPicker.module.css';


const summaryData = [
  { label: '–í—ã–ª–µ—Ç–µ–ª–∏ –≤–æ–≤—Ä–µ–º—è', value: 130, color: 'green', icon: '‚úÖ' },
  { label: '–° –∑–∞–¥–µ—Ä–∂–∫–æ–π > 15 –º', value: 28, color: 'yellow', icon: '‚ö†Ô∏è' },
  { label: '–û—Ç–º–µ–Ω–µ–Ω—ã', value: 3, color: 'red', icon: '‚ùå' },
  { label: '–†–µ–π—Å–æ–≤ –≤ –≤–æ–∑–¥—É—Ö–µ —Å–µ–π—á–∞—Å', value: 57, color: 'blue', icon: 'üõ´' },
];

const airportsData = [
  { name: '–®–µ—Ä–µ–º–µ—Ç—å–µ–≤–æ', onTime: 92, avgDelay: 5, canceled: 5 },
  { name: '–î–æ–º–æ–¥–µ–¥–æ–≤–æ', onTime: 87, avgDelay: 8, canceled: 9 },
  { name: '–í–ª–∞–¥–∏–≤–æ—Å—Ç–æ–∫', onTime: 83, avgDelay: 9, canceled: 4 },
  { name: '–ö–∞–∑–∞–Ω—å', onTime: 81, avgDelay: 10, canceled: 7 },
  { name: '–ü—É–ª–∫–æ–≤–æ', onTime: 77, avgDelay: 12, canceled: 10 },
  { name: '–°–æ—á–∏', onTime: 75, avgDelay: 14, canceled: 8 },
];

const PunctualityDashboard = ({ handleCountryChange }) => {
  const [countries, setCountries] = useState([]);
  return (
    <div style={{ maxWidth: 1000, margin: '20px auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏</h2>

      <div style={{ display: 'flex', gap: 20, marginBottom: 30, flexWrap: 'wrap' }}>
        {summaryData.map(({ label, value, color, icon }) => (
          <div key={label} style={{
            flex: '1 1 200px',
            backgroundColor: getColor(color),
            color: 'white',
            padding: '15px 20px',
            borderRadius: 12,
            boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
          }}>
            <div style={{ fontSize: 18, marginBottom: 6 }}>{icon} {label}</div>
            <div style={{ fontSize: 24, fontWeight: 'bold' }}>{value} —Ä–µ–π—Å–æ–≤</div>
          </div>
        ))}
      </div>

      <h3>–°–≤–æ–¥–∫–∞ –ø–æ –∞—ç—Ä–æ–ø–æ—Ä—Ç–∞–º</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse', backgroundColor: '#fff' }}>
        <thead>
          <tr style={{ backgroundColor: '#f5f5f5' }}>
            <th style={th}>–ê—ç—Ä–æ–ø–æ—Ä—Ç</th>
            <th style={th}>% –≤–æ–≤—Ä–µ–º—è</th>
            <th style={th}>–°—Ä. –∑–∞–¥–µ—Ä–∂–∫–∞</th>
            <th style={th}>–û—Ç–º–µ–Ω–µ–Ω–æ</th>
          </tr>
        </thead>
        <tbody>
          {airportsData.map(({ name, onTime, avgDelay, canceled }) => (
            <tr key={name}>
              <td style={td}>{name}</td>
              <td style={td}>{onTime} %</td>
              <td style={td}>{avgDelay} –º–∏–Ω</td>
              <td style={td}>{canceled}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PunctualityDashboard;
