import React from 'react';

import { Cards, CountryPicker, Chart } from './components';
import { fetchData } from './api/';
import styles from './App.module.css';

import image from './images/image.png';

class App extends React.Component {
  state = {
    data: {},
    country: '',
  }

  async componentDidMount() {
    const response = await fetch('/airports.json');
    const airports = await response.json();

    // Добавим случайные значения пунктуальности
    const enriched = airports.map(airport => {
      const totalFlights = Math.floor(Math.random() * 100) + 50;
      const onTime = Math.floor(totalFlights * (Math.random() * 0.5 + 0.5)); // 50–100% вовремя
      return {
        ...airport,
        totalFlights,
        onTime,
        delayed: totalFlights - onTime
      };
    });

    this.setState({ data: enriched });
  }

  handleCountryChange = async (country) => {
    const response = await fetch('/airports.json');  
    const data = await response.json();

    this.setState({ data, country: country });
  }

  render() {
    const { data, country } = this.state;

    return (
      <div className={styles.container}>
        <img className={styles.image} src={image} alt="COVID-19" />
        <Cards data={data} />
        <CountryPicker handleCountryChange={this.handleCountryChange} />
        <Chart data={data} country={country} /> 
      </div>
    );
  }
}

export default App;
