import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import iconUrl from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

const DefaultIcon = L.icon({
  iconUrl,
  shadowUrl: iconShadow,
});
L.Marker.prototype.options.icon = DefaultIcon;

const MapComponent = ({ airports = [] }) => {
  return (
    <MapContainer center={[60, 100]} zoom={3} scrollWheelZoom={true} style={{ height: '900px', width: '100%' }}>
      <TileLayer
        attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {airports.map((airport, i) => (
        <Marker key={i} position={[airport.latitude, airport.longitude]}>
          <Popup>
            <b>{airport['Название аэропорта']}</b><br />
            Город: {airport.city || airport['Город'] || '—'}<br />
            IATA: {airport['IATA код']}<br />
            Вылетов: {airport['Кол-во вылетов']}<br />
            Прилетов: {airport['Кол-во прилетов']}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default MapComponent;
